mapper.select:
SELECT count(*)
FROM
chat_room_member
WHERE room_id = #{roomId}
AND ucode = #{ucode}

SELECT role
FROM chat_room_member
WHERE room_id = #{roomId}
AND ucode = #{ucode}

SELECT count(*)
FROM chat_room_member
WHERE room_id = #{roomId}

mapper.update:
UPDATE chat_room
SET title=#{title}
WHERE
room_id=#{roomId}

mapper.delete:
DELETE FROM chat_room_member
WHERE room_id = #{roomId}
AND ucode = #{ucode}
