mapper.select:
SELECT count(*)
FROM member
WHERE id=#{id}

SELECT count(*)
FROM member
WHERE email=#{email}

SELECT pass
FROM member
WHERE ucode=#{ucode}

SELECT ucode,id,name,icon,pass,birth,email,phone
FROM member
WHERE id=#{id}

SELECT ucode,id,name,icon,pass,birth,email,phone
FROM member
WHERE ucode=#{ucode}

mapper.update:
UPDATE member
SET name=#{name}
WHERE ucode=#{ucode}

UPDATE member
SET name=#{name}, pass=#{pass}, birth = TO_DATE(#{birth}, 'YY/MM/DD'), phone=#{phone}
WHERE ucode=#{ucode}

UPDATE member
SET pass=#{pass}
WHERE ucode=#{ucode}
